$(document).ready(function() {

  // Fullpage libery was initiated.

  $('#fullpage').fullpage({
      scrollHorizontally: false,
      navigation: true,
      navigationPosition: 'left',
  });
  $.fn.fullpage.setAllowScrolling(false);


  // Slider for third section.

  $('.slider').slick({
      dots: false,
      infinite: true,
      speed: 500,
      slidesToShow: 3,
      slidesToScroll: 1,
      centerMode: true,
      centerPadding: '5',
      autoplay: true,
      autoplaySpeed: 2000,
      arrows: true,
      prevArrow: '<div class="slick-prev"><img src="images/arrow.svg" alt="arrow"></div>',
      nextArrow: '<div class="slick-next"><img src="images/arrow.svg" alt="arrow"></div>',
      responsive: [{
              breakpoint: 767,
              settings: {
                  slidesToShow: 2,
                  slidesToScroll: 1
              }
          },
          {
              breakpoint: 575,
              settings: {
                  arrows: false,
                  autoplaySpeed: 3000,
                  slidesToShow: 1,
                  slidesToScroll: 1
              }
          }
      ]
  });

});